const reducer = (state, action) => {
  switch (action.type) {
    case "LOGIN":
      localStorage.setItem("profile", JSON.stringify({ ...action?.data }));
      return { ...state, auth: { success: true, authData: action.payload } };

    case "LOGOUT":
      break;

    default:
      break;
  }
};
