import React, { useState, useEffect, useContext } from "react";
import { GlobalContext } from "../../context/GlobalState";
import { Link, useNavigate } from "react-router-dom";
import Logo from "../../images/logo.png";
import { GoogleLogout } from "react-google-login";

import {
  Header,
  LogoContainer,
  Avatar,
  UserContainer,
  LogoutButton,
  AppName,
} from "./AppbarElements";

const Appbar = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("profile")));
  const { logout } = useContext(GlobalContext);
  const navigate = useNavigate();

  useEffect(() => {
    setUser(JSON.parse(localStorage.getItem("profile")));
  }, []);

  const handleLogout = () => {
    logout(navigate);
  };

  return (
    <Header>
      <LogoContainer>
        <img src={Logo} alt="Logo" />
      </LogoContainer>

      <AppName>Expense Tracker</AppName>
      <UserContainer>
        <Avatar
          alt={user?.googleUserData?.name}
          src={user?.googleUserData?.imageUrl}
        />
        <GoogleLogout
          clientId="208706502819-6uhrqrva53mmm6nfjgmbjbijcf5dgtdg.apps.googleusercontent.com"
          buttonText="Logout"
          onLogoutSuccess={handleLogout}
          render={renderProps => {
            <LogoutButton onClick={renderProps.onClick} disabled={renderProps.disabled}>LOG OUT</LogoutButton>;
          }}
        ></GoogleLogout>
      </UserContainer>
    </Header>
  );
};

export default Appbar;
