import axios from "axios";

// const API = axios.create({baseURL: "http://localhost:5000"});
const API = axios.create();

// Passing bearer token in headers
API.interceptors.request.use((req) => {
  const {googleUserData} = localStorage.getItem("profile");
  console.log(googleUserData)
    if (googleUserData) {
      req.headers.authorization = `Bearer ${
        JSON.parse(googleUserData).token
      }`;
    }
    return req;
  });

export const fetchTransactions = () => API.get("/expensetracker/transactions/");
export const addTransaction = (transaction, config) => API.post("/expensetracker/transactions/", transaction, config);
export const deleteTransaction = (transactionID) => API.delete(`/expensetracker/transactions/${transactionID}`);

export const googleLogin = (token) => API.post("/expensetracker/users/auth", token);
