import { makeStyles } from "@mui/styles";

const useStyles = makeStyles(() => ({
    root: {},
    appbar: {
      background: "white",
      borderBottom: "1px solid rgb(236, 237, 239)",
      paddingTop: "5px",
    },
    toolbar: {
      display: "flex",
      flexDirection: "column",
      justifyContent: "center",
    },
    topbar:{
      width: "100%",
      height: "20px",

    },
    wrapper: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      maxWidth: '1100px',
      width: '100%',
    },
    wrapper2: {
      display: "flex",
      justifyContent: "space-between",
      // maxWidth: '1100px',
      width: '100%',
    },
    logoContainer: {
      display: 'flex',
      alignItems: 'center',
    },

    logo: {
      width: "100px",
      height: "50px",
      // paddingTop: "8px",
      filter: "grayscale(0%)",
      "&:hover": {
        transition: "0.5s ease-in-out",
        filter: "grayscale(70%)",

      },
    },

    buttonContainer: {
      display: "flex",
    },

    button: {
      margin: '2px',
      textTransform: "none",
      fontSize: '16px',
    },

    linkContainer: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
    link: {
      fontSize: '18px',
      margin: "0 10px",
      cursor: 'pointer',
      "&:hover": {
        textDecoration: "none",
        transition: "0.1s ease-in-out",

      },
    },
  logoLink: {
    fontSize: '18px',
    // margin: "0 10px",
    cursor: 'pointer',
    "&:hover": {
      textDecoration: "none",
      transition: "0.1s ease-in-out",

    },
  },
  dropdownMenuItemMail:{
    fontSize: '14px',
    color: "grey",
  },
  dropdownMenu: {
  },
}))

export default useStyles;
